import React, { Component } from 'react';

// Externals
import PropTypes from 'prop-types';

// Material helpers
import { withStyles } from '@material-ui/core';

// Material components
import { CircularProgress, Typography } from '@material-ui/core';

// Shared layouts
import { Dashboard as DashboardLayout } from 'layouts';

// Shared services
import { getUsers } from 'services/user';

// Custom components
import { UsersToolbar, UsersTable } from './components';

// Component styles
import styles from './style';

class UserList extends Component {
  signal = true;

  state = {
    isLoading: false,
    limit: 40,
    users: [],
    selectedUsers: [],
    searchedUsers: [],
    error: null
  };
  //
  // async getUsers() {
  //   try {
  //     this.setState({ isLoading: true });
  //
  //     const { limit } = this.state;
  //
  //     const { users } = await getUsers(limit);
  //
  //     if (this.signal) {
  //       this.setState({
  //         isLoading: false,
  //         users
  //       });
  //     }
  //   } catch (error) {
  //     if (this.signal) {
  //       this.setState({
  //         isLoading: false,
  //         error
  //       });
  //     }
  //   }
  // }


  componentDidMount() {
    this.signal = true;
    //this.getUsers();

    //this.setState({ isLoading: true });
    //console.log(this.state.limit);
    this.setState({ isLoading: true });
    this.getPatients(0,this.state.limit)
      .then((res) => {
        this.setState({ users: res});
        console.log(res);
        this.setState({ isLoading: false});
      })
      .catch((err) => {
        console.log(err);
        this.setState({ isLoading: false });
      });

  }

  async getPatients (begin, limit){
    const response = await fetch('/getPatients/' + begin + '/'+ limit );
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message)
    }
    return body;
  };

  componentWillUnmount() {
    this.signal = false;
  }

  handleSelect = selectedUsers => {
    this.setState({ selectedUsers });
  };

  renderUsers() {
    const { classes } = this.props;
    const { isLoading, users, error } = this.state;

    if (isLoading) {
      return (
        <div className={classes.progressWrapper}>
          <CircularProgress />
        </div>
      );
    }

    if (error) {
      return <Typography variant="h6">{error}</Typography>;
    }

    if (users.length === 0) {
      return <Typography variant="h6">There are no users</Typography>;
    }

    return (
      <UsersTable
        //
        onSelect={this.handleSelect}
        users={users}
      />
    );
  }

  render() {
    const { classes } = this.props;
    const { selectedUsers } = this.state;

    return (
      <DashboardLayout title="Patients">
        <div className={classes.root}>
          <UsersToolbar selectedUsers={selectedUsers} />
          <div className={classes.content}>{this.renderUsers()}</div>
        </div>
      </DashboardLayout>
    );
  }
}

UserList.propTypes = {
  className: PropTypes.string,
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(UserList);
